public class PositionalNotation {
    public static void main(String[] args) {
        /**
         * @Author: marzsccc
         * @intro:四种不同进制
         * @param {*}
         * @return {*}
         */
        // 0b开头是2进制
        int n1 = 0b1010;

        int n2 = 1010;
        // 0开头是8进制
        int n3 = 01010;
        // 0x开头是16进制
        int n4 = 0x1010101;

    }
}
